/* Get the list of posts paginated by Wordpress default script */
function pagination() {

	global $wp_query;

	$pagination =  '<div class="pagination">'.paginate_links(array(
		'base'     => get_pagenum_link(1) . '%_%',
		'format'   => 'page/%#%',
		'total'    => $wp_query->max_num_pages,
		'show_all' => true
		)).'</div>';
	return $pagination;
}

/* get post details */
function get_post_details($post) {
	$post_ID           = get_the_ID();	 		             // The Post ID
	$post_thumbnail    = "";              		          	 // The post specified Tumbnail (if exists)
	$post_meta         = get_post_metadata($post_ID,true);   // Retreive list of ranked Keywords and Tags

	if (has_post_thumbnail()) {
		$thumbnailsrc      = wp_get_attachment_image_src( get_post_thumbnail_id($post_ID), 'large' ) ;
		$post_thumbnail    = 'http://ahmadassaf.com/blog/wp-content/themes/blog/timthumb.php?src='.str_replace("localhost", "ahmadassaf.com", $thumbnailsrc[0]).'&h=200&w=300&zc=1"';
	}

	return array(
		"post_ID"             => $post_ID,
		"post_thumbnail_meta" => $thumbnailsrc[0],
		"post_thumbnail"      => $post_thumbnail,
		"post_permalink"      => get_permalink($post_ID),
		"post_publish_date"   => get_the_date('F j, Y'),
		"post_title"          => get_the_title(),
		"post_excerpt"        => get_the_excerpt(),
		"post_categories"     => $post_meta["categories"],
		"post_tags"           => count($post_meta["tags"]) > 5 ? array_slice($post_meta["tags"],0,5) : $post_meta["tags"]
	);
}

/* Get the post metadata including tags, keywords and categories */
function get_post_metadata($post_id, $fetch_categories) {

	$keywords  = $categories = $tags = $display_tags = array();

	$post_tags    = wp_get_post_tags($post_id,array('fields' => 'all','order'=> 'DESC', 'orderby' => 'count'));
	foreach ($post_tags as $tag) {
		if ($tag->name != "Featured") {
			$keywords[] = $tag->name;
			$tags[]     = array(
				"name" => $tag->name,
				"link" => get_tag_link($tag->term_id)
				);
		}
	}

	if ($fetch_categories) {
		$post_categories = wp_get_post_categories( $post_id );

		foreach($post_categories as $category){
			$cat = get_category( $category );
			$categories[] = array(
				'name' => $cat->name,
				'slug' => $cat->slug,
				'link' => get_category_link($cat->cat_ID)
				);
		}
	}

	$post_metadata = array(
		"keywords"   => $keywords,
		"tags"       => $tags,
		"categories" => $categories
		);

	return $post_metadata;
}

/* Build the Sub-Categories filter to show on each Category Parent */
function build_category_filters($category_id) {

	$categories =  get_categories(array('hide_empty' => 0, 'child_of'=>$category_id));
	$categories_filter = array();

	if (!empty($categories)) {
		foreach ($categories as $cat) {
			$category = get_term_by('name', $cat->name , 'category');
			$categories_filter[] = array(
				'name'  => $category->name,
				'slug'  => $category->slug,
				'count' => $category->count
				);
		}
	}
	return $categories_filter;
}

/* Build the comments counter - Taken from WP-Function comments_number */
function formated_comments_number( $zero = false, $one = false, $more = false ) {

	$number = get_comments_number();

	if ( $number > 1 )
		$output = str_replace('%', number_format_i18n($number), ( false === $more ) ? __('% Comments') : $more);
	elseif ( $number == 0 )
		$output = ( false === $zero ) ? __('No Comments') : $zero;
	else
		$output = ( false === $one ) ? __('1 Comment') : $one;

	return apply_filters('comments_number', $output, $number);
}

/* Get the Quote to display in HomePage randomly from the Database */
function get_quote() {
	global $wpdb;
	$quote = $wpdb->get_results
	("
		SELECT * FROM `arqmswin_quotes`
		AS r1 JOIN (SELECT ROUND(  RAND( ) * (SELECT MAX( id ) FROM `arqmswin_quotes`)) AS id )
		AS r2 WHERE r1.id >= r2.id ORDER BY r1.id ASC LIMIT 1;");
	return $quote;
}

/* Search highlight functions: Highlight search terms in Title and Excerpt */
function search_excerpt_highlight() {
	$excerpt = get_the_excerpt();
	$keys = implode('|', explode(' ', get_search_query()));
	$excerpt = preg_replace('/(' . $keys .')/iu', '<mark>\0</mark>', $excerpt);

	return '<p>' . $excerpt . '</p>';
}
function search_title_highlight() {
	$title = get_the_title();
	$keys = implode('|', explode(' ', get_search_query()));
	$title = preg_replace('/(' . $keys .')/iu', '<mark>\0</mark>', $title);

	return $title;
}

/* Replace the source attribute for images in the post to activate lazy loading */
function replace_img_src($content){
	$preloading_image = 'data:image/gif;base64,';
	if (!is_feed()) {
		$content = mb_convert_encoding($content, 'HTML-ENTITIES', "UTF-8");
		if ($content) {
			$document = new DOMDocument();
			libxml_use_internal_errors(true);
			$document->loadHTML(utf8_decode($content));
			libxml_use_internal_errors(false);
			libxml_clear_errors();

			$imgs = $document->getElementsByTagName('img');
			if ($imgs) {
				foreach($imgs as $key=>$value) {
						$img_src = $value->getAttribute('src');
						if ($value->hasAttribute("width") && $value->hasAttribute("height")) {
							$value->setAttribute('data-width',$value->getAttribute('width'));
							$value->setAttribute('data-height',$value->getAttribute('height'));
							$value->removeAttribute('height');
							$value->removeAttribute('width');
						}
						$value->setAttribute('alt',html_entity_decode(get_the_title(), ENT_NOQUOTES, 'UTF-8'));
						$value->setAttribute('data-src', $img_src);
						$value->setAttribute('src', $preloading_image);
						$value->setAttribute("onError","$(this).parents('.image').length !== 0 ? $(this).parents('.image').css('display','none') : $(this).css('display','none');");
				}
			}
			return preg_replace('~<(?:!DOCTYPE|/?(?:html|body))[^>]*>\s*~i', '', $document->saveHTML());
		}
	}

}

/* Remove Image Spceific width attribute when inserting to posts */
function remove_width_attribute( $html ) {
	$html = preg_replace( '/(width|height)="\d*"\s/', "", $html );
	return $html;
}

/* Remove the Wordpress version number from meta information */
function remove_version() {
