/* **************  My ShortCodes   *************** */
function linksBar($params = array()) {
	extract(shortcode_atts(array('download' => '', 'demo' => '','github'=> ''), $params));
	$buttonsBar =  '<div class="buttons_bar">';
	$buttonsBar .= $download == '' ? '' : '<span class="post_button_link download"><a href="'. $download .'"><i class="icon-download"></i>Link</a></span>';
	$buttonsBar .= $demo == '' ? '' : '<span class="post_button_link"><a href="'. $demo .'"><i class="icon-play-circled"></i>Demo</a></span>';
	$buttonsBar .= $github == '' ? '' : '<span class="post_button_link"><a href="'. $github .'"><i class="icon-github"></i>Github</a></span>';
	$buttonsBar .= '</div>';

	return $buttonsBar;
}

function related($params = array()) {
	$preloading_image = 'data:image/gif;base64,';
	extract(shortcode_atts(array('link' => '', 'title' => '', 'image' => ''), $params));
	$related =  '<div class="yarpp-related related"><div class="YARPP_wrapper"><article itemprop="blogPost" itemscope itemtype="http://schema.org/BlogPosting" class="post_preview">';
	$related.=	'<a class="image" href="'.$link.'" >';
	$related.=	'<img src="'.$preloading_image .'"onError="$(this).parents(".image").length !== 0 ? $(this).parents(".image").css("display","none") : $(this).css("display","none");" data-src="'.$image.'" alt="'.$title.'"/>';
	$related.=	'</a><article class="post"><h3 itemprop="name headline" class="title">';
	$related.=	'<a href="'.$link.'" rel="bookmark">'.$title.'</a>';
	$related.=	'</h3> </article></article>';
	$related .= '</div></div>';

	return $related;
}

function highlight($params = array()) {
